import { Component, EventEmitter, Input, Output, OnChanges, SimpleChanges, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { MascotaCl } from '../../../interfaces/mascota-cl';
import { MascotaService } from '../../../services/mascota.service';
import {HttpClient} from "@angular/common/http";
import {Observable} from "rxjs";
import {ClienteCl} from "../../../interfaces/cliente-cl";
import {ClienteService} from "../../../services/cliente.service";

@Component({
  selector: 'app-registro-dogs',
  templateUrl: './registro-dogs.component.html',
  styleUrls: ['./registro-dogs.component.css']
})
export class RegistroDogsComponent implements OnInit, OnChanges {
  @Input() userRole: string = 'editar';

  mascotaToEdit: MascotaCl | undefined;
  formMascota: MascotaCl = new MascotaCl('', '', 0, 0, '', false, '');
  clientes:ClienteCl[] = [];

  constructor(
    private router: Router,
    private route: ActivatedRoute,
    private mascotaService: MascotaService,private clienteService:ClienteService
  ) {}

  ngOnInit(): void {
    this.route.queryParams.subscribe(params => {
      const id = +params['id'];
      this.clienteService.getClientes().subscribe(
        (clientes) => {
          this.clientes = clientes;
        });
      if (id) {
        this.userRole = 'editar';
        this.mascotaService.getMascotaById(id).subscribe(mascota => {
          this.mascotaToEdit = mascota;
          if (this.mascotaToEdit) {
            this.formMascota = {...this.mascotaToEdit };
          }
        });
      }
      else {
        this.clienteService.getClientes().subscribe(
          (clientes) => {
          this.clientes = clientes;
        });
      }
    });
  }

  ngOnChanges(changes: SimpleChanges): void {
    if (changes['mascotaToEdit'] && this.mascotaToEdit) {
      this.formMascota = {...this.mascotaToEdit };
    }
  }

  addMascotaForm(): void {
    if (this.formMascota.cliente && this.formMascota.cliente.id) {
      if (this.formMascota.id) {
        this.mascotaService.updateMascota(this.formMascota, this.formMascota.cliente.id);
      } else {
        this.mascotaService.addMascota(this.formMascota, this.formMascota.cliente.id);
      }
    }
    this.formMascota = new MascotaCl('', '', 0, 0, '', false, '');
    this.router.navigate(['/mascotas']);
  }

}
<div id="clouds">
  <img id="centro1" src="../../../../assets/register/centro1.png">
  <img id="centro2" src="../../../../assets/register/centro2.png">
</div>
<div id="forms">
  <h1 class="registrar" *ngIf="userRole === 'registrar'">Registrar</h1>
  <h1 class="registrar" *ngIf="userRole === 'editar'">Editar</h1>
  <form (ngSubmit)="addMascotaForm()">
    <h2>Información Mascota</h2>

    <div class="form-input">
      <label for="nombre">Nombre:</label>
      <input type="text" id="nombre" [(ngModel)]="formMascota.nombre" name="nombre" required>
    </div>

    <div class="form-input">
      <label for="raza">Raza:</label>
      <input type="text" id="raza" [(ngModel)]="formMascota.raza" name="raza" required>
    </div>

    <div class="form-input">
      <label for="peso">Peso (kg):</label>
      <input type="number" id="peso" [(ngModel)]="formMascota.peso" name="peso" required>
    </div>

    <div class="form-input">
      <label for="edad">Edad (años):</label>
      <input type="number" id="edad" [(ngModel)]="formMascota.edad" name="edad" required>
    </div>

    <div class="form-input">
      <label for="foto">Imagen:</label>
      <input type="text" id="foto" [(ngModel)]="formMascota.imagen" name="Imagen" required>
    </div>

    <div class="form-input">
      <label for="enfermedad">Enfermedad:</label>
      <input type="text" id="enfermedad" [(ngModel)]="formMascota.enfermedad" name="enfermedad" required>
    </div>

    <div class="form-input">
      <label for="estado">Estado:</label>
      <input type="checkbox" id="estado" [(ngModel)]="formMascota.estado" name="estado">
    </div>

    <div  class="form-input">
      <label for="cliente">Cliente:</label>
      <select id="cliente" [(ngModel)]="formMascota.cliente" name="cliente" required>
        <option *ngFor="let cliente of clientes" [ngValue]="cliente">{{cliente.nombre}}</option>
      </select>
    </div>

    <div class="button-registrar">
      <button id="button-registrar-mascota" type="submit">Registrar</button>
    </div>
  </form>
</div>
