import {Component, OnInit} from '@angular/core';
import {ActivatedRoute, Router} from "@angular/router";
import {MascotaService} from "../../../services/mascota.service";
import {MascotaCl} from "../../../interfaces/mascota-cl";

@Component({
  selector: 'app-details',
  templateUrl: './details.component.html',
  styleUrls: ['./details.component.css']
})
export class DetailsComponent implements OnInit {
  constructor(private router: Router, private route : ActivatedRoute, private mascotaService : MascotaService) {}

  mascota : MascotaCl | undefined;

  ngOnInit(): void {
      this.route.queryParams.subscribe(params => {
      const id = +params['id'];
      this.mascotaService.getMascotaById(id).subscribe((mascota) => {
        this.mascota = mascota;
      });
    });
  }
}
<main>
  <div id="container">
    <div id="img-container">
      <img [src]="mascota?.imagen">
    </div>
    <div id="data-container">
      <div id="names">
        <div class="pet-name">Nombre: <span>{{ mascota?.nombre }}</span></div>
      </div>
      <div id="info-container">
        <div class="info">
          <p>Raza: <span>{{ mascota?.raza }}</span></p>
          <p>Edad: <span>{{ mascota?.edad }}</span> años</p>
          <p>Peso: <span>{{ mascota?.peso }}</span> Kg</p>
          <p>Enfermedad: <span>{{ mascota?.enfermedad }}</span></p>
          <p>Estado: <span>{{ mascota?.estado === true ? 'Inactivo' : 'Activo' }}</span></p>
        </div>
      </div>
    </div>
  </div>
</main>
