import {Component, OnChanges, OnInit, SimpleChanges} from '@angular/core';
import {ClienteCl} from "../../../interfaces/cliente-cl";
import {ClienteService} from "../../../services/cliente.service";
import {Router} from "@angular/router";

@Component({
  selector: 'app-table-client',
  templateUrl: './table-client.component.html',
  styleUrls: ['./table-client.component.css']
})
export class TableClientComponent implements OnInit{
  clientes: ClienteCl[] = [];

  constructor(private clienteService: ClienteService, private router: Router, ) {}

  ngOnInit(): void {
    this.refreshClientes();
  }

  deleteCliente(id: number): void {
    this.clientes = this.clientes.filter(cliente => cliente.id !== id);
    this.clienteService.deleteCliente(id);
  }

  editCliente(cliente: ClienteCl): void {
    this.router.navigate(['/registro-c'], { queryParams: { id: cliente.id } });
  }

  refreshClientes(): void {
    this.router.navigate(['/clientes']);
    this.clienteService.getClientes().subscribe(
      (clientes) => {
      this.clientes = clientes;
    });
  }
}
<div class="container">
  <div id="register-container">
    <h1>Clientes registrados</h1>
  </div>
  <table>
    <thead>
    <tr>
      <th>Cedula</th>
      <th>Nombre</th>
      <th>Correo</th>
      <th>Celular</th>
      <th>Acciones</th>
    </tr>
    </thead>
    <tbody>
    <tr *ngFor="let cliente of clientes">
      <td>{{ cliente.cedula }}</td>
      <td>{{ cliente.nombre }}</td>
      <td>{{ cliente.correo }}</td>
      <td>{{ cliente.celular }}</td>
      <td>
        <a id="editar" (click)="editCliente(cliente)">editar</a>
        <a id="eliminar" (click)="deleteCliente(cliente.id!)">eliminar</a>
      </td>
    </tbody>
  </table>
</div>
