import {Component, SimpleChanges} from '@angular/core';
import {ActivatedRoute, Router} from "@angular/router";
import {MascotaService} from "../../../services/mascota.service";

@Component({
  selector: 'app-login-dogs',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponentDogs {

  cedula : number = 0;
  contrase : string = '';
  constructor(    private mascotaService: MascotaService, private router: Router, private route : ActivatedRoute) {
  }

  checkLogin(){
   this.router.navigate(['/mascotas']);
  }
}
<main>
  <div id="logins">
    <div class="forms">
      <h1 class="registrar">Veterinario</h1>
      <form (ngSubmit)="checkLogin()">
        <div>
          <input [(ngModel)]="cedula" type="text" id="cedula-v" name="cedula" placeholder="Cédula">
        </div>
        <div>
          <input [(ngModel)]="contrase" type="password" id="password" name="password" placeholder="Contraseña">
        </div>
        <div class="button-registrar">
          <button type="submit" class="button-registrar2" >Ingresar</button>
        </div>
      </form>
    </div>
  </div>
</main>
