import {Component, Input, OnChanges, OnInit, SimpleChanges} from '@angular/core';
import {ActivatedRoute, Router} from "@angular/router";
import {ClienteService} from "../../../services/cliente.service";
import {VeterinarioService} from "../../../services/veterinario.service";
import {VeterinarioCl} from "../../../interfaces/veterinario-cl";

@Component({
  selector: 'app-registro-vet',
  templateUrl: './registro-vet.component.html',
  styleUrls: ['./registro-vet.component.css']
})
export class RegistroVetComponent implements OnInit, OnChanges {

  @Input() userRole: string = 'editar';

  veterinarioToEdit: VeterinarioCl | undefined;
  formVeterinario: VeterinarioCl = new VeterinarioCl('', '', '', '', '', 0);
  veterinarios : VeterinarioCl[] = [];

  constructor(
    private router: Router,
    private route: ActivatedRoute,
    private veterinarioService: VeterinarioService
  ) {}
  ngOnInit(): void {
    this.route.queryParams.subscribe(params => {
      const id = +params['id'];
      if (id) {
        this.userRole = 'editar';
        console.log(id);
        this.veterinarioService.getVeterinarioById(id).subscribe(veterinario => {
          console.log("veterinario",veterinario);
          this.veterinarioToEdit = veterinario;
          if (this.veterinarioToEdit) {
            this.formVeterinario = {...this.veterinarioToEdit };
          }
        });
      }
    });
  }

  ngOnChanges(changes: any): void {
    if (changes['clienteToEdit'] && this.veterinarioToEdit) {
      this.formVeterinario = {...this.veterinarioToEdit };
    }
  }

  addVeterinarioForm(): void {
    if (this.formVeterinario.id) {
      this.veterinarioService.updateVeterinario(this.formVeterinario);
    } else {
      this.veterinarioService.addVeterinario(this.formVeterinario);
    }
    this.formVeterinario = new VeterinarioCl('', '', '', '', '', 0);
    this.router.navigate(['/veterinarios']);
  }

}
<main>
<div id="clouds">
  <img id="centro1" src="../../../../assets/register/centro1.png">
  <img id="centro2" src="../../../../assets/register/centro2.png">
</div>

<div id="forms">
  <h1 class="registrar" *ngIf="userRole === 'registrar'">Registrar</h1>
  <h1 class="registrar" *ngIf="userRole === 'editar'">Editar</h1>
  <form (ngSubmit)="addVeterinarioForm()">
    <h2>Información Veterinario</h2>

    <div class="input-form">
      <label for="nombre">Nombre:</label>
      <input type="text" id="nombre" [(ngModel)]="formVeterinario.nombre" name="nombre" required>
    </div>

    <div class="input-form">
      <label for="raza">Especialidad:</label>
      <input type="text" id="raza" [(ngModel)]="formVeterinario.especialidad" name="raza" required>
    </div>

    <div class="input-form">
      <label for="foto">Imagen:</label>
      <input type="text" id="foto" [(ngModel)]="formVeterinario.foto" name="Imagen" required>
    </div>


    <div class="input-form">
      <label for="peso">Atenciones</label>
      <input type="number" id="peso" [(ngModel)]="formVeterinario.num_atenciones" name="peso" required>
    </div>

    <div class="button-registrar">
      <button id="button-registrar-mascota" type="submit">Registrar</button>
    </div>
  </form>
</div>

</main>
