import {Component, Input, OnChanges, OnInit} from '@angular/core';
import {ClienteCl} from "../../../interfaces/cliente-cl";
import {ActivatedRoute, Router} from "@angular/router";
import {ClienteService} from "../../../services/cliente.service";

@Component({
  selector: 'app-registro-client',
  templateUrl: './registro-client.component.html',
  styleUrls: ['./registro-client.component.css']
})
export class RegistroClientComponent implements OnInit, OnChanges {
  @Input() userRole: string = 'editar';

  clienteToEdit: ClienteCl | undefined;
  formCliente: ClienteCl = new ClienteCl(0,'','',0,undefined);

  constructor(
    private router: Router,
    private route: ActivatedRoute,
    private clienteService: ClienteService
  ) {}

  ngOnInit(): void {
    this.route.queryParams.subscribe(params => {
      const id = +params['id'];
      if (id) {
        this.userRole = 'editar';
        this.clienteService.getClienteById(id).subscribe(cliente => {
          console.log(cliente);
          this.clienteToEdit = cliente;
          if (this.clienteToEdit) {
            this.formCliente = {...this.clienteToEdit };
          }
        });
      }
    });
  }

  ngOnChanges(changes: any): void {
    if (changes['clienteToEdit'] && this.clienteToEdit) {
      this.formCliente = {...this.clienteToEdit };
    }
  }

  addClienteForm(): void {
    if (this.formCliente.id) {
      this.clienteService.updateCliente(this.formCliente);
    } else {
      this.clienteService.addCliente(this.formCliente);
    }
    this.formCliente = new ClienteCl(0,'','',0,undefined);
    this.router.navigate(['/clientes']);
  }
}
<div id="clouds">
  <img id="centro1" src="../../../../assets/register/centro1.png">
  <img id="centro2" src="../../../../assets/register/centro2.png">
</div>
<div id="forms">
  <h1 class="registrar" *ngIf="userRole === 'registrar'">Registrar</h1>
  <h1 class="registrar" *ngIf="userRole === 'editar'">Editar</h1>
  <form (ngSubmit)="addClienteForm()">
    <h2>Información Cliente</h2>

    <div class="form-input">
      <label for="cedula">Cedula:</label>
      <input type="text" id="cedula" [(ngModel)]="formCliente.cedula" name="cedula" required>
    </div>

    <div class="form-input">
      <label for="nombre">Nombre:</label>
      <input type="text" id="nombre" [(ngModel)]="formCliente.nombre" name="nombre" required>
    </div>

    <div class="form-input">
      <label for="correo">Correo:</label>
      <input type="email" id="correo" [(ngModel)]="formCliente.correo" name="correo" required>
    </div>

    <div class="form-input">
      <label for="celular">Celular:</label>
      <input type="text" id="celular" [(ngModel)]="formCliente.celular" name="celular" required>
    </div>


    <div class="button-registrar">
      <button id="button-registrar-mascota" type="submit">Registrar</button>
    </div>
  </form>
</div>
